import java.util.*;

public class Main {
	public static void main(String args[]){
		Scanner sc = new Scanner(System.in);
		String s;
		s = sc.next();

		Stack stack = new Stack();

		int cnt = 0;
		int res = 0;

		for(int j = 0; j < s.length(); j++) {
			if(s.charAt(j)=='(') {
				stack.push(1);
				cnt++;
			} else if(s.charAt(j)==')') {
				if(stack.size != 0) {
