import java.io.*;

public class Main {
  static int[] memo;
  public static int fibonacci(int n) {


    if (n <= 1) {
      memo[n] = n;
      return n;
    } else if (memo[n] != 0) {
      return memo[n];
    } else {
      return memo[n] = fibonacci(n-1) + fibonacci(n-2);
    }
